from maru.grammeme.abstract import Grammeme


class PartOfSpeech(Grammeme):
    NOUN = 'NOUN'
    ADJECTIVE = 'ADJ'
    PRONOUN = 'PRON'
    NUMERICAL = 'NUM'
    VERB = 'VERB'
    ADVERB = 'ADV'
    DETERMINANT = 'DET'
    CONJUNCTION = 'CONJ'
    ADPOSITION = 'ADP'
    PARTICLE = 'PART'
    INTERJECTION = 'INTJ'
    PUNCTUATION = 'PUNCT'
    INTRODUCTION = 'H'
    UNKNOWN = 'UNKN'
